import React from 'react';

const MatrixTable = () => {
  // Sample data representing class names and corresponding payment particulars
  const classData = [
    {
      className: 'Class 1',
      paymentParticulars: [
        { name: 'Admission Fee', amount: 2000 },
        { name: 'Tuition Fee', amount: 5000 },
        { name: 'Exam Fee', amount: 1000 }
      ]
    },
    {
      className: 'Class 2',
      paymentParticulars: [
        { name: 'Admission Fee', amount: 2500 },
        { name: 'Tuition Fee', amount: 6000 },
        { name: 'Exam Fee', amount: 1200 }
      ]
    },
    {
      className: 'Class 3',
      paymentParticulars: [
        { name: 'Admission Fee', amount: 3000 },
        { name: 'Tuition Fee', amount: 7000 },
        { name: 'Exam Fee', amount: 1500 } 
      ]
    },
      {
        className: 'Class 4',
        paymentParticulars: [
          { name: 'Admission Fee', amount: 2500 },
          { name: 'Tuition Fee', amount: 6000 },
          { name: 'Exam Fee', amount: 1200 }
        ]
      },
      {
        className: 'Class 5',
        paymentParticulars: [
          { name: 'Admission Fee', amount: 2500 },
          { name: 'Tuition Fee', amount: 6000 },
          { name: 'Exam Fee', amount: 1200 }
        ]
      },

      {
        className: 'Class 6',
        paymentParticulars: [
          { name: 'Admission Fee', amount: 2500 },
          { name: 'Tuition Fee', amount: 6000 },
          { name: 'Exam Fee', amount: 1200 }
        ]
      },
      {
        className: 'Class 7',
        paymentParticulars: [
          { name: 'Admission Fee', amount: 2500 },
          { name: 'Tuition Fee', amount: 6000 },
          { name: 'Exam Fee', amount: 1200 }
        ]
      },
      {
        className: 'Class 8',
        paymentParticulars: [
          { name: 'Admission Fee', amount: 2500 },
          { name: 'Tuition Fee', amount: 6000 },
          { name: 'Exam Fee', amount: 1200 }
        ]
      },


      {
        className: 'Class 9',
        paymentParticulars: [
          { name: 'Admission Fee', amount: 2500 },
          { name: 'Tuition Fee', amount: 6000 },
          { name: 'Exam Fee', amount: 1200 }
        ]
      },
      {
        className: 'Class 10',
        paymentParticulars: [
          { name: '', amount: 2500 },
          { name: 'Tuition Fee', amount: 6000 },
          { name: 'Exam Fee', amount: 1200 }
        ]
      },
      {
        className: 'Class 11',
        paymentParticulars: [
          { name: 'Admission Fee', amount: 2500 },
          { name: 'Tuition Fee', amount: 6000 },
          { name: 'Exam Fee', amount: 1200 }
        ]
      },
      {
        className: 'Class 12',
        paymentParticulars: [
          { name: 'Admission Fee', amount: 2500 },
          { name: 'Tuition Fee', amount: 6000 },
          { name: 'Exam Fee', amount: 1200 }
        ]
      },
    
  ];

  return (
    <table border="1">
      <thead>
        <tr>
          <th>Class Name</th>
          {classData[0].paymentParticulars.map((particular, index) => (
            <th key={index}>{particular.name}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {classData.map((classItem, idx) => (
          <tr key={idx}>
            <td>{classItem.className}</td>
            {classItem.paymentParticulars.map((particular, index) => (
              <td key={index}>{particular.amount}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default MatrixTable;
