import React, { useState, useEffect } from 'react';

const SearchBox = ({ handleSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    handleSearch(term);
  };

  return (
    <input
      type="text"
      placeholder="Search..."
      value={searchTerm}
      onChange={handleChange}
    />
  );
};

const Navbar = () => {
  const [formData, setFormData] = useState({
    ExpenseHead: '',
    ExpenseName: '',
    ExpenseDate: '',
    ExpenseAmount: '',
    ExpenseBy: '',
    VendorName: '',
    PaymentMode: '',
    BillNumber: '',
    Bill: ''
  });

  const [ExpenseName, setExpenseName] = useState([]);
  const [filteredVendorName, setFilteredVendorName] = useState([]);

  useEffect(() => {
    setFilteredExpenseName(ExpenseName);
  }, [ExpenseName]);

  const handleChange = (e, rowIndex, columnName) => {
    const { value } = e.target;
    const updatedExpenseName = ExpenseName.map((ExpenseName, index) => {
      if (index === rowIndex) {
        return {
          ...Vendor,
          [columnName]: value
        };
      }
      return ExpenseName;
    });
    setExpenseName(updatedExpenseName);
    setFilteredExpenseName(updatedVendor);
  };

  const handleAddRow = () => {
    setVendor([...VendorName, formData]);
    setFormData({
        ExpenseHead: '',
        ExpenseName: '',
        ExpenseDate: '',
        ExpenseAmount: '',
        ExpenseBy: '',
        VendorName: '',
        PaymentMode: '',
        BillNumber: '',
        Bill: ''
    });
  };

  const handleSearch = (searchTerm) => {
    console.log("Search term:", searchTerm);
    const filteredVendorName = VendorName.filter(Vendor => {
      return Object.values(Vendor).some(value =>
        value.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    console.log("Filtered accounts:", filteredVendorName);
    setFilteredVendorName(filteredVendorName);
  };

  return (
    <div className='containerStyle' style={{ backgroundColor: 'white' }}>
      <div className="header" style={{ color: 'black', backgroundColor: '#5DADE2', height: '10vh', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0 20px' }}>
        <div style={{ display: 'flex', alignItems: 'left' }}>
          <h1 style={{ margin: 0 }}>Expense Add</h1>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', height: '100%' }}>
          <label htmlFor="languageSelect" style={{ marginRight: '20px', marginBottom: 0 }}>Language:</label>
          <select id="languageSelect" style={{ height: '20%', marginRight: '20px' }}>
            <option value="english">English</option>
            <option value="hindi">Hindi</option>
          </select>
        </div>
        <a className="nav-link" href="/" style={{ position: 'absolute', left: 0, top: 0 }}>
          {/* Home Icon */}
        </a>
      </div>

      <div style={{ padding: '20px' }}>
        <form style={{ display: 'grid', gridTemplateColumns: '1fr', gap: '20px' }}>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="Vendor Name">Expense Head : </label>
            <input type="text" id="VendorName" name="VendorName" value={formData.VendorName} onChange={(e) => setFormData({ ...formData, VendorName: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorContact">Expense Name : </label>
            <input type="text" id="VendorContact" name="VendorContact" value={formData.VendorContact} onChange={(e) => setFormData({ ...formData, VendorContact: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorEMail">Expense Date : </label>
            <input type="text" id="VendorEMail" name="VendorEMail" value={formData.VendorEMail} onChange={(e) => setFormData({ ...formData, VendorEMail: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorAddress">Expense Amount : </label>
            <input type="text" id="VendorAddress" name="VendorAddress" value={formData.VendorAddress} onChange={(e) => setFormData({ ...formData, VendorAddress: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorRemark">Expense By : </label>
            <input type="text" id="VendorRemark" name="VendorRemark" value={formData.VendorRemark} onChange={(e) => setFormData({ ...formData, VendorRemark: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorRemark">Vendor Name : </label>
            <input type="text" id="VendorRemark" name="VendorRemark" value={formData.VendorRemark} onChange={(e) => setFormData({ ...formData, VendorRemark: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorRemark">Payment Mode : </label>
            <input type="text" id="VendorRemark" name="VendorRemark" value={formData.VendorRemark} onChange={(e) => setFormData({ ...formData, VendorRemark: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorRemark">Bill Number : </label>
            <input type="text" id="VendorRemark" name="VendorRemark" value={formData.VendorRemark} onChange={(e) => setFormData({ ...formData, VendorRemark: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorRemark">Bill : </label>
            <input type="text" id="VendorRemark" name="VendorRemark" value={formData.VendorRemark} onChange={(e) => setFormData({ ...formData, VendorRemark: e.target.value })} />
          </div>


          <div style={{ textAlign: 'center' }}>
            <button className="btn btn-outline-success" type="button" onClick={handleAddRow}>Submit</button>
          </div>
        </form>
      </div>

      <div className="header" style={{ backgroundColor: '#5DADE2', height: '10vh', display: 'flex', alignItems: 'left', justifyContent: 'left' }}>
        <h1 style={{ margin: 0 }}>Expense List</h1>
      </div>

      <div className="search-box" style={{ padding: '20px', textAlign: 'right' }}>
        {/* Render the SearchBox component here */}
        <SearchBox handleSearch={handleSearch} />
      </div>

      {/* Render the table below */}
      <div className="table-responsive" style={{ padding: '20px' }}>
        <table className="table" style={{ width: '100%' }}>
          <thead>
            <tr>
              <th>ExpenseHead</th>
              <th>ExpenseName</th>
              <th> ExpenseDate</th>
              <th>ExpenseAmount</th>
              <th>ExpenseBy</th>
              <th>VendorName</th>
              <th>PaymentMode</th>
              <th>BillNumber</th>
              <th>Bill</th>
            </tr>
          </thead>
          <tbody>
            {/* Map through filteredVendorName and render each row */}
            {filteredVendorName?.map((Vendor, rowIndex) => (
              <tr key={rowIndex}>
                {Object.entries(Vendor).map(([columnName, value], colIndex) => (
                  <td key={colIndex}>
                    <div className="form-group">
                      <input
                        type="text"
                        value={value}
                        onChange={(e) => handleChange(e, rowIndex, columnName)}
                        className="form-control"
                        style={{ width: '100%' }}
                      />
                    </div>
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Navbar;
