import React, { useState, useEffect } from 'react';

const Navbar = () => {
  const [formData, setFormData] = useState({
    VendorName: '',
    VendorContact: '',
    VendorEMail: '',
    VendorAddress: '',
    VendorRemark: '',
    UpdatedBy: '',
    Date: ''
  });

  const [VendorName, setVendorName] = useState([]);
  const [filteredVendorName, setFilteredVendorName] = useState([]);

  useEffect(() => {
    setFilteredVendorName(VendorName);
  }, [VendorName]);

  const handleChange = (e, rowIndex, columnName) => {
    const { value } = e.target;
    const updatedVendorName = filteredVendorName.map((vendor, index) => {
      if (index === rowIndex) {
        return {
          ...vendor,
          [columnName]: value
        };
      }
      return vendor;
    });
    setVendorName(updatedVendorName);
    setFilteredVendorName(updatedVendorName);
  };

  const handleAddRow = () => {
    setVendorName([...VendorName, formData]);
    setFormData({
      VendorName: '',
      VendorContact: '',
      VendorEMail: '',
      VendorAddress: '',
      VendorRemark: '',
      UpdatedBy: '',
      Date: ''
    });
  };

  const handleSearch = (searchTerm) => {
    console.log("Search term:", searchTerm);
    const filteredVendors = VendorName.filter(vendor => {
      return Object.values(vendor).some(value =>
        value.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    console.log("Vendor Name:", filteredVendors);
    setFilteredVendorName(filteredVendors);
  };

  return (
    <div className='containerStyle' style={{ backgroundColor: 'white' }}>
      {/* Rest of your JSX */}
    </div>
  );
};

export default Navbar;
