import React, { useState } from 'react';

const SearchBox = ({ handleSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
    handleSearch(e.target.value);
  };

  return (
    <input
      type="text"
      placeholder="Search..."
      value={searchTerm}
      onChange={handleChange}
    />
  );
};

const Navbar = () => {
  const [formData, setFormData] = useState({
    accountType: '',
    bankName: '',
    branchName: '',
    accountHolderName: '',
    accountNumber: '',
    bankIFSCCode: '',
    merchantKey: '',
    bankAddress: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
    // After submission, clear the form data
    setFormData({
      accountType: '',
      bankName: '',
      branchName: '',
      accountHolderName: '',
      accountNumber: '',
      bankIFSCCode: '',
      merchantKey: '',
      bankAddress: ''
    });
  };

  const handleSearch = (searchTerm) => {
    // Handle search functionality here
    // You can filter the bank account list based on the search term
    console.log("Searching for:", searchTerm);
  };

  return (
    <div className='containerStyle' style={{ backgroundColor: 'white' }}>
      <div style={{ color: 'black', backgroundColor: '#5DADE2', height: '10vh', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0 20px' }}>
        <div style={{ display: 'flex', alignItems: 'Left' }}>
          <h1 style={{ margin: 0 }}>Account Management</h1>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', height: '100%' }}>
          <label htmlFor="languageSelect" style={{ marginRight: '20px', marginBottom: 0 }}>Language:</label>
          <select id="languageSelect" style={{ height: '20%', marginRight: '20px' }}>
            <option value="english">English</option>
            <option value="hindi">Hindi</option>
          </select>
        </div>
        <a className="nav-link" href="/" style={{ position: 'absolute', left: 0, top: 0 }}>
          {/* Home Icon */}
        </a>
      </div>

      <div style={{ padding: '20px' }}>
        <h2>Bank Account Add</h2>
        <hr style={{ width: '100%', height: '2px', backgroundColor: 'black', margin: '10px 0' }} />

        <form onSubmit={handleSubmit} style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr 1fr', gap: '20px' }}>
          {/* Form Inputs */}
        </form>
      </div>

      <div style={{ backgroundColor: '#5DADE2', height: '10vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <h1>Bank Account List</h1>
        <div style={{ marginLeft: '20px' }}>
          <SearchBox handleSearch={handleSearch} />
        </div>
      </div>
    </div>
  );
};

export default Navbar;
