import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';

const Navbar = () => {
  const [formData, setFormData] = useState({
    accountType: '',
    bankName: '',
    branchName: '',
    accountHolderName: '',
    accountNumber: '',
    bankIFSCCode: '',
    merchantKey: '',
    bankAddress: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
    // After submission, clear the form data
    setFormData({
      accountType: '',
      bankName: '',
      branchName: '',
      accountHolderName: '',
      accountNumber: '',
      bankIFSCCode: '',
      merchantKey: '',
      bankAddress: ''
    });
  };

  return (
    <div className='containerStyle' style={{ alignItems: 'center', backgroundColor: 'white' }}>
      <div style={{ color: 'black', textAlign: 'left', backgroundColor: '#5DADE2', height: '15vh', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0 20px' }}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <h1 style={{ margin: 0 }}>Account Management</h1>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', height: '100%' }}>
          <label htmlFor="languageSelect" style={{ marginRight: '20px', marginBottom: 0 }}>Language:</label>
          <select id="languageSelect" style={{ height: '20%', marginRight: '20px' }}>
            <option value="english">English</option>
            <option value="hindi">Hindi</option>
          </select>
        </div>
        <a className="nav-link" href="/" style={{ position: 'absolute', left: 0, top: 0 }}>
          {/* <FontAwesomeIcon icon={faHome} style={{ background: 'none', height: '20px', marginTop: '20px' }} /> */}
        </a>
      </div>

      <div style={{ display: 'flex', gap: '100px' }}>
       
        <h2>Add New User Details</h2>
      </div>

      <hr style={{ width: '100%', height: '2px', backgroundColor: 'black', margin: '10px 0' }} />

      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'left', justifyContent: 'flex-start', marginLeft: '20px' }}>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="accountType">Account Type :  </label>
            <input type="text" id="accountType" name="accountType" value={formData.accountType} onChange={handleChange} />
          </div>
          {/* Repeat similar structure for other input fields */}
          <button className="btn btn-outline-success" type="submit" style={{ marginTop: '20px' }}>Submit</button>
        </form>
      </div>

      <div style={{ display: 'flex', gap: '100px' }}>
        {/* <h2>Bank Account List</h2> */}
      </div>
    </div>
  );
};

export default Navbar;
