import React, { useState, useEffect } from 'react';

const SearchBox = ({ handleSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    handleSearch(term);
  };

  return (
    <input
      type="text"
      placeholder="Search..."
      value={searchTerm}
      onChange={handleChange}
    />
  );
};

const Navbar = () => {
  const [formData, setFormData] = useState({
    VendorName: '',
    VendorContact: '',
    VendorEMail: '',
    VendorAddress: '',
    VendorRemark: '',
    UpdatedBy: '',
    Date: ''
  });

  const [VendorName, setVendorName] = useState([]);
  const [filteredVendorName, setFilteredVendorName] = useState([]);

  useEffect(() => {
    setFilteredVendorName(VendorName);
  }, [VendorName]);

  const handleAddRow = () => {
    setVendorName([...VendorName, formData]);
    setFormData({
      VendorName: '',
      VendorContact: '',
      VendorEMail: '',
      VendorAddress: '',
      VendorRemark: '',
      UpdatedBy: '',
      Date: ''
    });
  };

  const handleSearch = (searchTerm) => {
    console.log("Search term:", searchTerm);
    const filteredVendors = VendorName.filter(vendor => {
      return Object.values(vendor).some(value =>
        value.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    console.log("Vendor Name:", filteredVendors);
    setFilteredVendorName(filteredVendors);
  };

  return (
    <div className='containerStyle' style={{ backgroundColor: 'white' }}>
      {/* Rest of your JSX */}
    </div>
  );
};

export default Navbar;
