const Navbar = () => {
  const [formData, setFormData] = useState({
    VendorName: '',
    VendorContact: '',
    VendorEmail: '',
    VendorAddress: '',
    VendorRemark: '',
    UpdatedBy: '',
    Date: '',
    Action: ''
  });

  const [VendorName, setVendorName] = useState([]);
  const [filteredVendorName, setFilteredVendorName] = useState([]);

  useEffect(() => {
    setFilteredVendorName(VendorName);
  }, [VendorName]);

  const handleChange = (e, rowIndex, columnName) => {
    const { value } = e.target;
    const updatedVendor = filteredVendorName.map((vendor, index) => {
      if (index === rowIndex) {
        return {
          ...vendor,
          [columnName]: value
        };
      }
      return vendor;
    });
    setFilteredVendorName(updatedVendor);
  };

  const handleAddRow = () => {
    setVendorName([...VendorName, formData]);
    setFormData({
      VendorName: '',
      VendorContact: '',
      VendorEmail: '',
      VendorAddress: '',
      VendorRemark: '',
      UpdatedBy: '',
      Date: '',
      Action: ''
    });
  };

  const handleVendorContactChange = (e) => {
    const contact = e.target.value;
    // Regular expression to match only numeric values
    const numericRegex = /^[0-9]*$/;
    if (numericRegex.test(contact) && contact.length <= 10) {
      // Valid numeric value and limited to 10 digits, update state
      setFormData({ ...formData, VendorContact: contact });
    }
  };

  const handleVendorEmailChange = (e) => {
    const email = e.target.value;
    const emailRegex = /^\S+@\S+\.\S+$/;
    if (emailRegex.test(email) || email === '') {
        // Valid email address or empty string, update state
        setFormData(prevFormData => ({ ...prevFormData, VendorEmail: email }));
    } else {
        // Invalid email format, you might want to provide feedback to the user
        console.log("Invalid email format");
        // Optionally, you can clear the email field or show an error message to the user
        // setFormData(prevFormData => ({ ...prevFormData, VendorEmail: '' }));
    }
  };

  const handleSearch = (searchTerm) => {
    console.log("Search term:", searchTerm);
    const filteredVendorName = VendorName.filter(Vendor => {
      return Object.values(Vendor).some(value =>
        value.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    console.log("Filtered accounts:", filteredVendorName);
    setFilteredVendorName(filteredVendorName);
  };

  return (
    <div className='containerStyle' style={{ backgroundColor: 'white' }}>
      {/* Header */}
      <div style={{ color: 'black', backgroundColor: '#5DADE2', height: '10vh', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0 20px' }}>
        <div style={{ display: 'flex', alignItems: 'left' }}>
          <h1 style={{ margin: 0 }}>Vendor Add</h1>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', height: '100%' }}>
          <label htmlFor="languageSelect" style={{ marginRight: '20px', marginBottom: 0 }}>Language:</label>
          <select id="languageSelect" style={{ height: '20%', marginRight: '20px' }}>
            <option value="english">English</option>
            <option value="hindi">Hindi</option>
          </select>
        </div>
        <a className="nav-link" href="/" style={{ position: 'absolute', left: 0, top: 0 }}>
          {/* Home Icon */}
        </a>
      </div>

      {/* Form */}
      <div style={{ padding: '20px' }}>
        <form style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr 1fr', gap: '20px' }}>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorName">Vendor Name:</label>
            <input type="text" id="VendorName" name="VendorName" placeholder="Vendor Name" value={formData.VendorName} onChange={(e) => setFormData({ ...formData, VendorName: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorContact">Vendor Contact:</label>
            <input type="text" id="VendorContact" name="VendorContact" placeholder="Vendor Contact" value={formData.VendorContact} onChange={handleVendorContactChange} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorEmail">Vendor Email:</label>
            <input type="text" id="VendorEmail" name="VendorEmail" placeholder="Vendor Email" value={formData.VendorEmail} onChange={handleVendorEmailChange} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorAddress">Vendor Address:</label>
            <input type="text" id="VendorAddress" name="VendorAddress" placeholder="Vendor Address" value={formData.VendorAddress} onChange={(e) => setFormData({ ...formData, VendorAddress: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="VendorRemark">Vendor Remark:</label>
            <input type="text" id="VendorRemark" name="VendorRemark" placeholder="Vendor Remark" value={formData.VendorRemark} onChange={(e) => setFormData({ ...formData, VendorRemark: e.target.value })} />
          </div>
          <div style={{ gridColumn: 'span 4', textAlign: 'center' }}>
            <button className="btn btn-outline-success" type="button" onClick={handleAddRow}>Submit</button>
          </div>
        </form>
      </div>

      {/* Vendor List */}
      <div style={{ backgroundColor: '#5DADE2', height: '10vh', display: 'flex', alignItems: 'left', justifyContent: 'left' }}>
        <h1>Vendor List</h1>
      </div>

      {/* Search Box */}
      <div style={{ marginLeft: '1300px' }}>
        <SearchBox handleSearch={handleSearch} />
      </div>

      {/* Table */}
      <div style={{ padding: '20px' }}>
        <table style={{ width: '100%' }}>
          <thead>
            <tr>
              <th>VendorName</th>
              <th>VendorContact</th>
              <th>VendorEmail</th>
              <th>VendorAddress</th>
              <th>VendorRemark</th>
              <th>UpdatedBy</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {/* Map through filteredVendorName and render each row */}
            {filteredVendorName?.map((vendor, rowIndex) => (
              <tr key={rowIndex}>
                {Object.entries(vendor).map(([columnName, value], colIndex) => (
                  <td key={colIndex}>
                    <input
                      type="text"
                      value={value}
                      onChange={(e) => handleChange(e, rowIndex, columnName)}
                    />
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Navbar;
