import React, { useState, useEffect } from 'react';

const SearchBox = ({ handleSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    handleSearch(term);
  };

  return (
    <input
      type="text"
      placeholder="Search..."
      value={searchTerm}
      onChange={handleChange}
    />
  );
};

const Navbar = () => {
  const [formData, setFormData] = useState({
    VendorName: '',
    VendorContact: '',
    VendorEMail: '',
    VendorAddress: '',
    VendorRemark: '',
    UpdatedBy: '',
    Date: '',
    Action: ''
  });

  const [Vendor, setVendor] = useState([]);
  const [filteredVendor, setFilteredBankAccounts] = useState([]);

  useEffect(() => {
    setFilteredBankAccounts(bankAccounts);
  }, [bankAccounts]);

  const handleChange = (e, rowIndex, columnName) => {
    const { value } = e.target;
    const updatedBankAccounts = bankAccounts.map((account, index) => {
      if (index === rowIndex) {
        return {
          ...account,
          [columnName]: value
        };
      }
      return account;
    });
    setBankAccounts(updatedBankAccounts);
    setFilteredBankAccounts(updatedBankAccounts);
  };

  const handleAddRow = () => {
    setBankAccounts([...bankAccounts, formData]);
    setFormData({
      VendorName: '',
      VendorContact: '',
      VendorEMail: '',
      VendorAddress: '',
      VendorRemark: '',
      UpdatedBy: '',
      Date: '',
      Action: ''
    });
  };

  const handleSearch = (searchTerm) => {
    console.log("Search term:", searchTerm);
    const filteredAccounts = bankAccounts.filter(account => {
      return Object.values(account).some(value =>
        value.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    console.log("Filtered accounts:", filteredAccounts);
    setFilteredBankAccounts(filteredAccounts);
  };

  return (
    <div className='containerStyle' style={{ backgroundColor: 'white' }}>
      <div style={{ color: 'black', backgroundColor: '#5DADE2', height: '10vh', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0 20px' }}>
        <div style={{ display: 'flex', alignItems: 'left' }}>
          <h1 style={{ margin: 0 }}>Vendor Add</h1>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', height: '100%' }}>
          <label htmlFor="languageSelect" style={{ marginRight: '20px', marginBottom: 0 }}>Language:</label>
          <select id="languageSelect" style={{ height: '20%', marginRight: '20px' }}>
            <option value="english">English</option>
            <option value="hindi">Hindi</option>
          </select>
        </div>
        <a className="nav-link" href="/" style={{ position: 'absolute', left: 0, top: 0 }}>
          {/* Home Icon */}
        </a>
      </div>

      <div style={{ padding: '20px' }}>
        
       

        <form style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr 1fr', gap: '20px' }}>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="accountType">Vendor Name : </label>
            <input type="text" id="accountType" name="accountType" value={formData.accountType} onChange={(e) => setFormData({ ...formData, accountType: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="bankName">Vendor Contact : </label>
            <input type="text" id="bankName" name="bankName" value={formData.bankName} onChange={(e) => setFormData({ ...formData, bankName: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="branchName">Vendor EMail : </label>
            <input type="text" id="branchName" name="branchName" value={formData.branchName} onChange={(e) => setFormData({ ...formData, branchName: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="accountHolderName">Vendor Address : </label>
            <input type="text" id="accountHolderName" name="accountHolderName" value={formData.accountHolderName} onChange={(e) => setFormData({ ...formData, accountHolderName: e.target.value })} />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="Vendor Remark">Vendor Remark : </label>
            <input type="text" id="accountNumber" name="accountNumber" value={formData.accountNumber} onChange={(e) => setFormData({ ...formData, accountNumber: e.target.value })} />
          </div>
          <div style={{ gridColumn: 'span 4', textAlign: 'center' }}>
            <button className="btn btn-outline-success" type="button" onClick={handleAddRow}>submit</button>
          </div>
        </form>
      </div>

      <div style={{ backgroundColor: '#5DADE2', height: '10vh', display: 'flex', alignItems: 'left', justifyContent: 'left' }}>
        <h1>Vendor List</h1>
      </div>

      <div style={{ marginLeft: '1300px' }}>
        {/* Render the SearchBox component here */}
        <SearchBox handleSearch={handleSearch} />
      </div>

      {/* Render the table below */}
      <div style={{ padding: '20px' }}>
        <table style={{ width: '100%' }}>
          <thead>
            <tr>
              <th>VendorName</th>
              <th>VendorContact</th>
              <th>VendorEMail</th>
              <th>VendorAddress</th>
              <th>VendorRemark</th>
              <th>UpdatedBy</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {/* Map through filteredBankAccounts and render each row */}
            {filteredBankAccounts?.map((account, rowIndex) => (
              <tr key={rowIndex}>
                {Object.entries(account).map(([columnName, value], colIndex) => (
                  <td key={colIndex}>
                    <input
                      type="text"
                      value={value}
                      onChange={(e) => handleChange(e, rowIndex, columnName)}
                    />
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Navbar;
