import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';

const Navbar = () => {
  const handleButtonClick = () => {
    // Add your button click logic here
    console.log("Button clicked!");
  };

  return (
    <div className='containerStyle' style={{ alignItems: 'center', backgroundColor: 'white' }}>
      <div style={{ color: 'black', textAlign: 'center', backgroundColor: '#5DADE2 ', height: '10vh', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div style={{ flexGrow: 1 }}> {/* Container for Home and Account Management */}
          <h2 style={{ margin: 0 }}>Home</h2>
          <h2 style={{ margin: 0 }}>Account Management</h2>
        </div>
        <div> {/* Container for Language dropdown */}
          <label htmlFor="languageSelect">Select Language:</label>
          <select id="languageSelect">
            <option value="en">English</option>
            <option value="es">Spanish</option>
            <option value="fr">French</option>
            {/* Add more language options as needed */}
          </select>
        </div>
        <a className="nav-link" href="">
          <FontAwesomeIcon icon={faHome} style={{ background: 'none', height: '26px', marginTop: '35px', marginRight: '20px' }} />
        </a>
      </div>
      
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '20px' }}>
        <hr style={{ width: '100%', height: '2px', backgroundColor: 'black', margin: '0' }} />
        <input type="button" value="Button" onClick={handleButtonClick} style={{ marginTop: '10px' }} />
      </div>
    </div>
  );
};

export default Navbar;
