import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle, faHome } from '@fortawesome/free-solid-svg-icons';

const Navbar = () => {
  return (
    <div style={{ marginTop: '10px', padding: '20px', height: 'auto', display: 'flex', flexWrap: 'wrap', justifyContent: 'center', textAlign:'center' }}>
                {/* First Box */}
                <div
    className='Data-Exploration'
    style={{
        color: 'black',
        height: 'auto',
        width: '50vmin',
        padding: '10px',
        fontSize: '20px',
        borderBottom: '1px solid #02133805',
        boxShadow: '2px 2px 5px 2px rgba(0, 0, 0, 0.1)',
        borderRadius: '10px',
        background: 'linear-gradient(to right, #F4D03F, #E6E6FA)',
        position: 'relative',
        overflow: 'hidden' ,
        transition: 'background-color 0.3s ease', // Added transition for smooth color change
        marginLeft: '50px', // Add margin between the boxes
    }}
>
 
<FontAwesomeIcon icon={faDollarSign} style={{ color: '#B7950B',height: '56px' }} />
 
 
        <h3>Fees management</h3>
    </div>
 
  {/* second Box */}
  <div
    className='Data-Exploration'
    style={{
        color: 'black',
        height: 'auto',
        width: '50vmin',
        padding: '10px',
        fontSize: '20px',
        borderBottom: '1px solid #02133805',
        boxShadow: '2px 2px 5px 2px rgba(0, 0, 0, 0.1)',
        borderRadius: '10px',
        background: 'linear-gradient(to right, #A3E4D7, #E6E6FA)',
        position: 'relative',
        overflow: 'hidden' ,
        transition: 'background-color 0.3s ease', // Added transition for smooth color change
        marginLeft: '50px', // Add margin between the boxes
    }}
>
 
<FontAwesomeIcon icon={faUserCheck} style={{ color: '#2874A6',height: '56px' }} />
 
 
        <h3>Attendance management</h3>
    </div>
 
    </div>
  );
};

export default Navbar;
