import React, { useState, useEffect } from 'react';

const SearchBox = ({ handleSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    handleSearch(term);
  };

  return (
    <input
      type="text"
      placeholder="Search..."
      value={searchTerm}
      onChange={handleChange}
      className="form-control mb-3"
    />
  );
};

const Navbar = () => {
  const [formData, setFormData] = useState({
    VendorName: '',
    VendorContact: '',
    VendorEMail: '',
    VendorAddress: '',
    VendorRemark: '',
    UpdatedBy: '',
    Date: '',
    Action: ''
  });

  const [VendorName, setVendor] = useState([]);
  const [filteredVendorName, setFilteredVendorName] = useState([]);

  useEffect(() => {
    setFilteredVendorName(VendorName);
  }, [VendorName]);

  const handleChange = (e, rowIndex, columnName) => {
    const { value } = e.target;
    const updatedVendor = VendorName.map((Vendor, index) => {
      if (index === rowIndex) {
        return {
          ...Vendor,
          [columnName]: value
        };
      }
      return Vendor;
    });
    setVendor(updatedVendor);
    setFilteredVendorName(updatedVendor);
  };

  const handleAddRow = () => {
    setVendor([...VendorName, formData]);
    setFormData({
      VendorName: '',
      VendorContact: '',
      VendorEMail: '',
      VendorAddress: '',
      VendorRemark: '',
      UpdatedBy: '',
      Date: '',
      Action: ''
    });
  };

  const handleSearch = (searchTerm) => {
    console.log("Search term:", searchTerm);
    const filteredVendorName = VendorName.filter(Vendor => {
      return Object.values(Vendor).some(value =>
        value.toLowerCase().includes(searchTerm.toLowerCase())
      );
    });
    console.log("Filtered accounts:", filteredVendorName);
    setFilteredVendorName(filteredVendorName);
  };

  return (
    <div className='container'>
      <div className="bg-primary text-white p-3 d-flex justify-content-between align-items-center">
        <h1 className="m-0">Vendor Add</h1>
        <div>
          <label htmlFor="languageSelect" className="mr-3 mb-0">Language:</label>
          <select id="languageSelect" className="form-select">
            <option value="english">English</option>
            <option value="hindi">Hindi</option>
          </select>
        </div>
        <a className="nav-link" href="/">
          {/* Home Icon */}
        </a>
      </div>

      <form className="row g-3">
        <div className="col-md-6">
          <label htmlFor="VendorName" className="form-label">Vendor Name :</label>
          <input type="text" id="VendorName" name="VendorName" value={formData.VendorName} onChange={(e) => setFormData({ ...formData, VendorName: e.target.value })} className="form-control" />
        </div>
        <div className="col-md-6">
          <label htmlFor="VendorContact" className="form-label">Vendor Contact :</label>
          <input type="text" id="VendorContact" name="VendorContact" value={formData.VendorContact} onChange={(e) => setFormData({ ...formData, VendorContact: e.target.value })} className="form-control" />
        </div>
        <div className="col-md-6">
          <label htmlFor="VendorEMail" className="form-label">Vendor EMail :</label>
          <input type="text" id="VendorEMail" name="VendorEMail" value={formData.VendorEMail} onChange={(e) => setFormData({ ...formData, VendorEMail: e.target.value })} className="form-control" />
        </div>
        <div className="col-md-6">
          <label htmlFor="VendorAddress" className="form-label">Vendor Address :</label>
          <input type="text" id="VendorAddress" name="VendorAddress" value={formData.VendorAddress} onChange={(e) => setFormData({ ...formData, VendorAddress: e.target.value })} className="form-control" />
        </div>
        <div className="col-md-6">
          <label htmlFor="VendorRemark" className="form-label">Vendor Remark :</label>
          <input type="text" id="VendorRemark" name="VendorRemark" value={formData.VendorRemark} onChange={(e) => setFormData({ ...formData, VendorRemark: e.target.value })} className="form-control" />
        </div>
        <div className="col-12 d-flex justify-content-center">
          <button className="btn btn-outline-success" type="button" onClick={handleAddRow}>Submit</button>
        </div>
      </form>

      <div className="bg-primary text-white p-3 d-flex justify-content-center align-items-center mt-3">
        <h1 className="m-0">Vendor List</h1>
      </div>

      <div className="table-responsive">
        <SearchBox handleSearch={handleSearch} />
        <table className="table table-bordered mt-3">
          <thead className="bg-primary text-white">
            <tr>
              <th>Vendor Name</th>
              <th>Vendor Contact</th>
              <th>Vendor EMail</th>
              <th>Vendor Address</th>
              <th>Vendor Remark</th>
              <th>Updated By</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredVendorName.map((vendor, rowIndex) => (
              <tr key={rowIndex}>
                {Object.entries(vendor).map(([columnName, value], colIndex) => (
                  <td key={colIndex}>
                    <input
                      type="text"
                      value={value}
                      onChange={(e) => handleChange(e, rowIndex, columnName)}
                      className="form-control"
                    />
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Navbar;
