import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle, faHome } from '@fortawesome/free-solid-svg-icons';

const Navbar = () => {
  return (
    <div className='containerStyle' style={{ alignItems: 'center', backgroundColor: 'white' }}>
      <div style={{ color: 'black', textAlign: 'center', backgroundColor: '#5DADE2', height: '10vh', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div style={{ textAlign: 'center', flexGrow: 1 }}> {/* Container for Account Management and Year */}
          <h1 style={{ margin: 0 }}>Education System</h1>
          <p style={{ margin: 0 }}>2023-2024</p>
        </div>
        <div> {/* Container for Language dropdown */}
          <label htmlFor="languageSelect">Select Language:</label>
          <select id="languageSelect">
            <option value="en">English</option>
            <option value="es">Hindi</option>
            <option value="fr">Marathi</option>
            {/* Add more language options as needed */}
          </select>
        </div>
        <a className="nav-link" href="">
          <FontAwesomeIcon icon={faHome} style={{ background: 'none', height: '26px', marginTop: '35px', marginRight: '20px' }} />
        </a>
      </div>

      <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginTop: '20px' }}>
        <h2 style={{ margin: '0 10px' }}>Account Management</h2>
      </div>

      <hr style={{ width: '100%', height: '2px', backgroundColor: 'black', margin: '20px 0' }} /> {/* Line below "Home" and "Account Management" */}

      <div style={{ display: 'flex', gap: '150px', justifyContent: 'left', marginTop: '100px' }}>
        
      
 <div style={{ backgroundImage: "url('images (1).jpg')", backgroundSize: 'cover', backgroundPosition: 'center', backgroundRepeat: 'no-repeat', height: '10vh', display: 'flex', justifyContent: 'center', alignItems: 'center',borderRadius:'30px', flexDirection: 'column' }}>
 <button
                style={{
                  padding: '10px 5px', // Adjust padding for button size
                  fontSize: '19px', // Adjust font size for button text
                  backgroundColor: 'white',
                  color: 'black',
                  borderRadius: '10px',
                  cursor: 'pointer',
                  transitionDuration: '0.2s',
                  border: 'none', // Remove border
                  fontStyle:'bold'
                }}
                // Adding hover effect
                onMouseOver={(e) => { e.target.style.backgroundColor = 'lightblue'; }}
                onMouseOut={(e) => { e.target.style.backgroundColor = 'white'; }}
              >
                Let's Talk
               
               
              </button></div>
        <button className="btn btn-secondary btn-large">Fee Management</button> {/* Added btn-large class */}
      </div>
    </div>
  );
};

export default Navbar;
